import React from 'react';
import styled from 'styled-components';

import { mainTheme } from '../styles/colors';

import addPhotoSVG from '../assets/add_a_photo-24px.svg';

export const Thumbnail = (props) => {
  return (
    <Container>
      <Img src={props.photoUrl ? props.photoUrl : addPhotoSVG} />
    </Container>
  );
};

const Container = styled.div`
   border: 1px solid ${mainTheme.midGrey};
   padding 16px;

   width 240px;

   margin-bottom: auto;

   background-color: ${mainTheme.coolGrey};

   box-shadow: 5px 5px 15px -2px ${mainTheme.shadowGrey};

   align-items: center;
   justify-content: center;
`;

const Img = styled.img`
   width 100%;
   margin: auto;
`;

export default Thumbnail;
