import React, { useState } from 'react';
import styled from 'styled-components/macro';

import { mainTheme } from '../styles/colors';
import { H2Style } from '../styles/headings';

export const MODE_OPTIONS = {
  view: 0,
  edit: 1
};

const EditablePane = ({ children = [] }) => {
  const [uiMode, setUIMode] = useState(MODE_OPTIONS.view);

  return (
    <Container>
      {uiMode === MODE_OPTIONS.view && (
        <ControlsContainer>
          <EditLink>Edit</EditLink>
        </ControlsContainer>
      )}
      {children}
    </Container>
  );
};

const Container = styled.div`
  display: flex;
  flex-direction: row;

  position: relative;

  width 100%;

  max-width: 1024px;

  padding: 24px;

  &:not(:first-child) {
   border-top: 1px solid ${mainTheme.borderGrey};
  }
`;

const ControlsContainer = styled.div`
  display: flex;
  flex-direction: row;

  position: absolute;

  right: 24px;
  top: 24px;
`;

const EditLink = styled.div`
  color: ${mainTheme.linkBlue};

  ${H2Style};

  @media screen and (hover: hover) {
    &:hover {
      cursor: pointer;
    }
  }
`;

export default EditablePane;
