import React, { useState } from 'react';
import styled from 'styled-components';

import Caret from './Caret';

import { mainTheme } from '../styles/colors';

export const DEFAULT_EXPANDED_WIDTH = 128;
export const DEFAULT_COLLAPSED_WIDTH = 12;

export const HorizontalExpand = ({
	collapsedWidth,
	expandedWidth,
	...props
}) => {
	const [isOpen, setIsOpen] = useState(false);

	return (
		<Container
			isOpen={isOpen}
			expandedWidth={expandedWidth}
			collapsedWidth={collapsedWidth}
			onClick={() => setIsOpen(!isOpen)}
		>
			<Caret isToggled={isOpen} />

			{/* we are intentionally rendering things when the container is collapsed so that children don't need to remount */}
			<ContentContainer isOpen={isOpen}> 
				{props.children}
			</ContentContainer>
		</Container>
	)
};

const Container = styled.div`
	border: 1px solid ${mainTheme.midGrey};
	background: ${mainTheme.transparentGrey};
	padding 16px;

	height: 100%;
	overflow-x: hidden;

	${props => props.isOpen ? `
		width: ${props.expandedWidth || DEFAULT_EXPANDED_WIDTH}px;
	`
	: `
		width: ${props.collapsedWidth || DEFAULT_COLLAPSED_WIDTH}px;

	`}

	transition: width 0.5s ease;

	align-items: center;
	justify-content: center;
`;

const ContentContainer = styled.div`
	visibility: ${props => props.isOpen ? 'visible' : 'hidden'};
`;

export default HorizontalExpand;
