import React from 'react';
import styled from 'styled-components';
import Select from 'react-dropdown-select';

// assuming 12 hour time
const HourOptions = [
  '12:00',
  '1:00',
  '2:00',
  '3:00',
  '4:00',
  '5:00',
  '6:00',
  '7:00',
  '8:00',
  '9:00',
  '10:00',
  '11:00'
];

const AM_PM = [
  ' AM',
  ' PM'
];

const options = [...HourOptions.map(hour => hour + AM_PM[0]), ...HourOptions.map(hour => hour + AM_PM[1])];

const HourSelector = ({
  onChange = () => {}
}) => {

  return (
    <Select
      options={options}
      onChange={onChange}
    />
  );
};

export default HourSelector;
