import React from 'react';
import styled from 'styled-components/macro';

import TitleHeader from './TitleHeader';

import { H2Style } from '../styles/headings';

const HEADER_COPY = 'Hours of Operation';
const SUB_HEAD_COPY = 'Manage standard hours of operation when providers are available to provide care. Patients will be informed if they submit an exam outside of these hours.';

const HoursOfOperation = ({ uiMode = -1 }) => {
  return (
    <Container>
      <TitleHeader header={HEADER_COPY} subhead={SUB_HEAD_COPY} />
      <Grid>
        <RowLabel>Sunday</RowLabel>
        <RowLabel>CLOSED</RowLabel>
        <RowLabel>9:00 AM - 5:00 PM</RowLabel>
      </Grid>
    </Container>
  );
};

const Container = styled.div`
  display: flex;
  flex-direction: column;

  width 100%;
`;

const Grid = styled.div`
  display: grid;

  grid-template-columns: 100px 64px 1fr;
  grid-template-rows: repeat(1fr);
`;

const RowLabel = styled.div`
  color: ${mainTheme.textGrey};
  
  ${H2Style}
`;

export default HoursOfOperation;
