import React from 'react';
import { connect } from 'react-redux';
import styled from 'styled-components';

import Thumbnail from './components/Thumbnail';
import { getPhotos } from './state/selectors/photos';
import { mainTheme } from './styles/colors';
import { MQ_FOR_DESKTOP } from './media';
import ExpandableNav from './components/ExpandableSideNav';

const App = ({ photos }) => {
  // HOCs and Routing
  return (
    <AppContainer id='app-outer-boundary'>
      <ExpandableNav />
      {photos.map(photo => <Thumbnail key={photo.id} photoUrl={photo.url} />)}
    </AppContainer>
  );
};

const mapStateToProps = state => ({
  photos: getPhotos(state)
});

export default connect(mapStateToProps)(App);

const AppContainer = styled.div`
  display: flex;
  flex-direction: row;
  width: 100vw;
  min-height: 100vh;
  background-color: ${mainTheme.blackPearlGrey};

