// font weights
export const regular = 400;
export const mediumBold = 500;

export const H1Style = `
  font-size: 24px;
  line-height: 24px;
  font-weight: ${mediumBold};
`;

export const H2Style = `
  font-size: 16px;
  line-height: 16px;
  font-weight: ${mediumBold};
`;

export const H3Style = `
  font-size: 12px;
  line-height: 12px;
  font-weight: ${regular};
`;
