import React, { useState } from 'react';
import styled from 'styled-components';

import { mainTheme } from '../styles/colors';

export const DEFAULT_EXPANDED_WIDTH = 128;
export const DEFAULT_COLLAPSED_WIDTH = 12;

export const HorizontalExpand = ({
	collapsedWidth,
	expandedWidth,
	...props
}) => {
	const [isOpen, setIsOpen] = useState(false);

	return (
		<Container
			isOpen={isOpen}
			expandedWidth={expandedWidth}
			collapsedWidth={collapsedWidth}
			onClick={() => setIsOpen(!isOpen)}
		>
			<Caret />

			{/* we are intentionally rendering things when the container is collapsed so that children don't need to remount */}
			<ContentContainer isOpen={isOpen}> 
				{props.children}
			</ContentContainer>
		</Container>
	)
};

const Container = styled.div`
	border: 1px solid ${mainTheme.midGrey};
	background: ${mainTheme.transparentGrey};
	padding 16px;

	height: 100%;
	overflow-x: hidden;

	${props => props.isOpen ? `
		width: ${props.expandedWidth || DEFAULT_EXPANDED_WIDTH}px;
	`
	: `
		width: ${props.collapsedWidth || DEFAULT_COLLAPSED_WIDTH}px;

	`}

	transition: width 0.5s ease;

	align-items: center;
	justify-content: center;
`;

const ContentContainer = styled.div`
	visibility: ${props => props.isOpen ? 'visible' : 'hidden'};
`;

const Caret = styled.div`
	position: relative;

	${props => props.isToggled && `
		transform: rotate(270deg);
	`}
  
  	::before {
	content: '';
	position: absolute;
	top: 0;
	left: 0;
	border-right: 12px solid #ebebeb;
	border-top: 12px solid transparent;
	border-bottom: 12px solid transparent;
  }
  
  ::after {
	content: '';
	position: absolute;
	left: 6px;
	top: 6px;
	border-right: 6px solid #fff;
	border-top: 6px solid transparent;
	border-bottom: 6px solid transparent;
  }
`;

export default HorizontalExpand;
