import React from 'react';
import styled from 'styled-components';

import { mainTheme } from '../styles/colors';

export const Caret = ({
  isToggled = false,
  ...props
}) => (
  <Container isToggled={isToggled} {...props}>
    <Triangle />
  </Container>
);

const Container = styled.div`
   position: relative;
   width: 24px;
   height: 24px;

   transform: rotate(90deg);

   transition: transform 0.5s ease;

   ${props => props.isToggled && `
      transform: rotate(-90deg) translateY(50%);
   `}
`;

const Triangle = styled.div`
   position: absolute;
   margin: auto;
   top: -6px;
   left: 0;
   right: 0;
   width: 12px;
   height: 12px;
   transform: rotate(45deg);
   border-right: 4px solid ${mainTheme.merchantMarineBlue};
   border-bottom: 4px solid ${mainTheme.merchantMarineBlue};
`;

export default Caret;
