import PHOTOS from '../action-constants/photos';

export const photosReducerKey = 'photosReducer';

const initialState = {
  photos: [
    { id: 'add-photo' } // add photo placeholder
  ]
};

const photosReducer = (state = initialState, action = {}) => {
  switch (action.type) {
    case PHOTOS.ADD_PHOTO:
      return {
        ...state,
        newPhoto: action.payload
      };
    default:
      return state;
  }
};

export default photosReducer;
